import React, { Component } from 'react'
import Calendar from 'react-calendar'
import Button from './time_buttons'


export default class MakeReservation extends Component {
    constructor(props){
        super(props)
        this.state ={
            name: "",
            phone: "",
            count:"",
            date:"",
        }
    }

        render(){
            return (
                <>
                <main>
                    {/* <dev> */}
                        <form ref={(el) => this.myForm = el} className='form-inline'>
                            <input placeholder='ФИО' onChange={(e) => this.setState({name: e.target.value})}/>
                            <input placeholder='Телефо' onChange={(e) => this.setState({phone: e.target.value})}/>
                            <input placeholder='Количество людей не больше 5' onChange={(e) => this.setState({lastname: e.target.value})}/>
                            {/* <input placeholder='Время' onChange={(e) => this.setState({lastname: e.target.value})}/> */}
                            <label id='time'>Выбирите время</label>
                            <div className='timeButtons'>
                                {this.generate_buttons()}
                            </div>
                            <button type='button' onClick={this.send_data}>ADD</button>

                        </form>
                    {/* </dev> */}
                </main>

                <aside>
                    <Calendar onChange={this.normal_data} />
                </aside>
                </>

            )
          }

          normal_data(el){
            var day = el.getDate();
            var month = el.getMonth();
            var year = el.getFullYear();

            var normal_data = year+"-"+month+"-"+day

            console.log(normal_data)
          }

         generate_buttons(){
            var list_buttons = []


            for(let a =  10.00; a <= 20.00; a+=0.50){
                if(Math.floor((a % 1) * Math.pow(10, 2)) === 50)
                    list_buttons.push(<Button time={Math.trunc(a)+":"+30}/>)
                else
                    list_buttons.push(<Button time={Math.trunc(a)+":00"}/>)

            }

            return list_buttons
         }

         setChosenTime(){
            var el = document.getElementById("time")
            el.set
         }

}
