import React, { Component } from 'react'
import Calendar from 'react-calendar'
// import { formatDate } from 'react-calendar/dist/cjs/shared/dateFormatter'
import Reservations from '../components/Reservations'


class Second extends Component {

  // data = [
  //   {date:"2023-10-13",
  //     name:"Denis",
  //     phone: "8402847493020",
  //     time: "10:30"},


  //     {date:"2023-11-13",
  //     name:"Denis",
  //     phone: "8402847493020",
  //     time: "23:30",
  //     count_person: 2},
  // ]

  constructor(props){
      super(props)
      this.state ={
          items: [],
          items1:[],
          data:"",
    }


    this.normal_data = this.normal_data.bind(this)

}


  render() {

    return (
      <>
        <main id="main_div">
          {this.state.items}
        </main>

        <aside>
          <Calendar onChange={this.normal_data} />
        </aside>
      </>

    )
  }

  normal_data(el){
    var day = el.getDate();
    var month = el.getMonth();
    var year = el.getFullYear();

    var normal_data = year+"-"+month+"-"+day
    this.state.data = normal_data
    document.getElementById('main_div').innerHTML = '';
    this.state.items.push(<Reservations users={this.state.items}/>)

  }
}

export default Second
