import React, { Component } from 'react'

export default class MakeReservation extends Component {

        render(){
            return (
                <main>
                    <dev class=".reservation">
                        <form ref={(el) => this.myForm = el}>
                            <input placeholder='Name' onChange={(e) => this.setState({firstname: e.target.value})}/>
                            <input placeholder='LastName' onChange={(e) => this.setState({lastname: e.target.value})}/>
                            <textarea placeholder='Way' onChange={(e) => this.setState({bio: e.target.value})}></textarea>
                            <input placeholder='Age' onChange={(e) => this.setState({age: e.target.value})}/>
                            <button type='button' onClick={() => {

                                    this.myForm.reset()


                                    this.props.onAdd({
                                    firstname: this.state.first_name,
                                    lastname: this.state.last_name,
                                    bio: this.state.bio,
                                    age: this.state.age,
                                    isHappy: this.state.isHappy,

                                })}
                            }>ADD</button>

                        </form>
                    </dev>
                </main>

            )
          }

}
